import { Component, Renderer2, ElementRef } from '@angular/core';
@Component({
  selector: 'app-doc-truyen',
  templateUrl: './doc-truyen.component.html',
  styleUrls: ['./doc-truyen.component.css']
})
export class DocTruyenComponent {
  showConsoleMessage(): void {
    console.log("Nút đã được nhấn!");
  }
  changeBackgroundColor(): void {
    var color = (document.getElementById("colorSelect") as HTMLSelectElement).value;
    var colorbg = (document.getElementsByClassName("story-container")[0] as HTMLDivElement)
    colorbg.style.backgroundColor = color;
  }
  toggleDropdown(): void {
    var dropdown = ((document.getElementById("dropdown")) as HTMLDivElement);
    if (dropdown.style.display === "none" || dropdown.style.display === "") {
      dropdown.style.display = "block";
    } else {
      dropdown.style.display = "none";
    }
  }
  comment: String[] = []
  abc(): void {
    let a = (document.getElementById('chat-input') as HTMLInputElement).value
    this.comment.push(a)
  }
  constructor(private renderer: Renderer2, private el: ElementRef) { }
  openForm(): void {
    const myForm = this.el.nativeElement.querySelector('#myForm');
    this.renderer.setStyle(myForm, 'display', 'block');
  }

  closeForm(): void {
    const myForm = this.el.nativeElement.querySelector('#myForm');
    this.renderer.setStyle(myForm, 'display', 'none');
  }
  changeFont() {
    var font = (document.getElementById("fontSelect") as HTMLSelectElement).value;
    var fonta = (document.getElementsByClassName("story-container")[0] as HTMLDivElement)
    fonta.style.fontFamily = font;
  }

  private fontSize = 16; // Kích cỡ chữ mặc định (đơn vị px)



  increaseFontSize() {
    this.fontSize += 2; // Tăng kích cỡ chữ lên 2px (hoặc giá trị tùy chọn)
    this.applyFontSize();
  }

  decreaseFontSize() {
    this.fontSize -= 2; // Giảm kích cỡ chữ đi 2px (hoặc giá trị tùy chọn)
    this.applyFontSize();
  }

  private applyFontSize() {
    const element = this.el.nativeElement.querySelector('.story-container');
    this.renderer.setStyle(element, 'font-size', `${this.fontSize}px`);
  }
}
